import React from 'react'
import { GET_MY_BOATS } from '../../queries'
import { useQuery } from '@apollo/client'

const PersonWithBoats = ({ display, id }) => {

    const styles = ({
        container: {
            display: display,
            position: 'relative',
            top: '20px',
        },
        list: {
            listStyleType: 'none',
            display: 'grid',
            gridGap: '10px',
            padding: '10px'
        },
        child: {
            border: 'solid 1px'
        }
    })

    const { loading, error, data } = useQuery(GET_MY_BOATS, { variables: { personId: id } })
    if (loading) return '..'
    if (error) return `Errror! ${error.message}`
    return (
        <div style={styles.container}>
            {console.log('data=>', data.personWithboats)}
            <h3>My Boats</h3>
            <ul style={styles.list}>
                {
                    data.personWithboats.map(item => {
                        return <li style={styles.child}>Model: {item.model}, Year: {item.year}, Price: ${item.price}, Make:{item.make}</li>
                    })
                }
            </ul>
        </div>
    )
}

export default PersonWithBoats;
